<?include_once("../inc/fungsi.php");include_once("../inc/config.php");$timeHariIni = date("Y-m-d");$sqlBanKiri = "SELECT * FROM cni_banner 											WHERE letak_banner = '4' 											AND status_banner = '1' 											AND (selesai_banner >= '".$timeHariIni."'   												AND mulai_banner <= '".$timeHariIni."')											AND (limit_banner > jumlah_show)											AND tgl_show = '".$timeHariIni."'   											ORDER BY rand() 											LIMIT 1																						";																				$hslBanKiri = mysql_query($sqlBanKiri);while($brsBanKiri = mysql_fetch_array($hslBanKiri)) {$filename="../images/banner/".$brsBanKiri['file_banner'];	if(file_exists($filename))										{	header("Content-type: image/jpeg");	header("Content-Length: ".filesize($filename)); 		$handle = fopen($filename, "rb");	$contents = fread($handle, filesize($filename));	echo $contents;	fclose($handle);	}}?>